#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\flutter\contacts\node_modules\.pnpm\@pnpm+win-x64@8.15.0\node_modules\@pnpm\win-x64\node_modules;D:\flutter\contacts\node_modules\.pnpm\@pnpm+win-x64@8.15.0\node_modules\@pnpm\node_modules;D:\flutter\contacts\node_modules\.pnpm\@pnpm+win-x64@8.15.0\node_modules;D:\flutter\contacts\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/flutter/contacts/node_modules/.pnpm/@pnpm+win-x64@8.15.0/node_modules/@pnpm/win-x64/node_modules:/mnt/d/flutter/contacts/node_modules/.pnpm/@pnpm+win-x64@8.15.0/node_modules/@pnpm/node_modules:/mnt/d/flutter/contacts/node_modules/.pnpm/@pnpm+win-x64@8.15.0/node_modules:/mnt/d/flutter/contacts/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

# Support pipeline input
if ($MyInvocation.ExpectingInput) {
  $input | & "$basedir/../../../../../../@pnpm+win-x64@8.15.0/node_modules/@pnpm/win-x64/pnpm.exe"   $args
} else {
  & "$basedir/../../../../../../@pnpm+win-x64@8.15.0/node_modules/@pnpm/win-x64/pnpm.exe"   $args
}
$env:NODE_PATH=$env_node_path
exit $LASTEXITCODE
